<?phprequire ('./../config.php');require ('./../include/functions.php');$mysql_connect_id = mysql_connect($config ['host'], $config ['user_datebase'], $config ['password_datebase']);mysql_select_db($config ['database']);mysql_query ("SET NAMES 'UTF8'");$sql = "UPDATE ls_orders set number_declaration = '".$_GET['number_declaration']."' where id = '".$_GET['id']."';";mysql_query ($sql);print "<span style=\"color:red;\"><b>Номер декларації успішно збережено</span>";$sql = "SELECT `id`,`value` FROM ls_settings;";$results = mysql_query($sql);$number = mysql_num_rows ($results);for ($i=0; $i<$number; $i++){		$array_config_param[] = mysql_fetch_array($results, MYSQL_ASSOC);	}foreach ($array_config_param as $key => $v){	$config['user_params_'.$v['id']] = $v['value'];}if (isset ($_COOKIE['lang']) or isset ($_GET['lang'])){	if (isset ($_COOKIE['lang']))	{		$alt_name_online_lang = $_COOKIE['lang'];	} else {		$alt_name_online_lang = $_GET['lang'];	}	$info_for_my_lang = return_info_for_lang_for_alt_name($alt_name_online_lang);	$id_online_lang = $info_for_my_lang['id'];} else {	$info_for_my_lang = return_info_for_lang_for_alt_name($config ['default_language']);	$id_online_lang = $info_for_my_lang['id'];}if ($config['user_params_6']){	$sql = "SELECT * FROM `ls_orders` where id='".$_GET['id']."';";	$result = mysql_query($sql);	$info_order = mysql_fetch_array($result, MYSQL_ASSOC);	$results = mysql_query("SELECT `value` FROM `ls_reference_values_translate` where `id_reference_value` = '".$info_order['dostavka']."' and `id_lang` = '".$id_online_lang."' limit 0,1;");	$info_dostavka = mysql_fetch_array($results, MYSQL_ASSOC);	$sms = "Dostavka: ".$info_dostavka['value']."%0A".$_GET['number_declaration']."%0AOtrumyvatu: ".$info_order['ship_date'];	//print "<br>";	//print strlen($sms);	go_sms (str_replace (' ', '', $info_order['number_phone']), $sms);}?>