<?phprequire ('./../config.php');$mysql_connect_id = mysql_connect($config ['host'], $config ['user_datebase'], $config ['password_datebase']);mysql_select_db($config ['database']);mysql_query ("SET NAMES 'utf-8'");switch ($_GET['type']){	case "email":		$sql = "UPDATE ls_orders set email = '".mysql_escape_string($_GET['value'])."' where id = '".$_GET['id']."';";		$text = 'E-mail';	break;	case "pib":		$sql = "UPDATE ls_orders set pib = '".mysql_escape_string($_GET['value'])."' where id = '".$_GET['id']."';";		$text = 'П.І.Б. ';	break;	case "number_phone":		$sql = "UPDATE ls_orders set number_phone = '".mysql_escape_string($_GET['value'])."' where id = '".$_GET['id']."';";		$text = 'Номер телефону ';	break;	case "adress":		$sql = "UPDATE ls_orders set adress = '".mysql_escape_string($_GET['value'])."' where id = '".$_GET['id']."';";		$text = 'Місто ';	break;	case "dostavka":		$sql = "UPDATE ls_orders set dostavka = '".mysql_escape_string($_GET['value'])."' where id = '".$_GET['id']."';";		$text = 'Спосіб доставки ';	break;	case "admin_note":		$sql = "UPDATE ls_orders set admin_note = '".mysql_escape_string($_GET['value'])."' where id = '".$_GET['id']."';";		$text = 'Нотатки адміна ';	break;}if (mysql_query ($sql)){	print "<span style=\"color:red;\"><b>".$text." збережено</span>";} else {	print "<span style=\"color:red;\"><b>Помилка при збереженні</span>";}?>