<?phpif (isset ($_POST['id_item'])){	require ('../config.php');	require ('functions.php');	$mysql_connect_id = mysql_connect($config ['host'], $config ['user_datebase'], $config ['password_datebase']);	mysql_select_db($config ['database']);	mysql_query ("SET NAMES 'utf-8'");	$lang_file_array = return_my_language ();	foreach ($lang_file_array as $v)	{		require_once($v);	}	//print_r ($_POST['param']);	if (isset ($_POST['param']))	{		foreach ($_POST['param'] as $key => $v)		{			$param .= $v;			if (isset ($_POST['param'][$key+1]))			{				$param .= '|';			}		}	} else {		$param = '';	}	$sql = "	INSERT INTO  `ls_cart` (	`id_item` ,	`uniq_user` ,	`other_param` ,	`time`	)	VALUES (	'".$_POST['id_item']."',	'".$_COOKIE['PHPSESSID']."' ,	'".$param."' ,	'".time()."'	);	";	if (mysql_query ($sql))	{		print '<span id="add_to_cart">'.$lang[363].'</span>';	} else {		print '<span id="add_to_cart_error">'.$lang[364].'</span>';	}}?>